import React, {useState, useEffect} from 'react';
import { Card, Icon, Modal, Button } from 'antd';
import { connect } from 'react-redux';

import './App.css';
import Nav from './Nav';

const { Meta } = Card;



/* Initialize News API */
//const NewsAPI = require('newsapi');
const APIkey = '920fe1871ec5410289dba3174398da12';


function ScreenArticlesBySource( props ) {

  const [ articles, setArticles ] = useState([]);
  const [ visible, setVisible ] = useState(false);

  useEffect( () => {
		async function fetchNews () {
			let urlRequestNews = `https://newsapi.org/v2/everything?sources=${props.match.params.id}&from=2020-02-09&to=2020-02-10&apiKey=${APIkey}`;
			let articles = await fetch( urlRequestNews );
			let response = await articles.json();

			if(response.status === 'ok'){
				console.log(response.articles)
				setArticles( response.articles )
			}
		}
		fetchNews();	
	}, [props] );

	const showModal = () => {
		setVisible(true);

	}

	const handleOk = e => {
		//console.log(e);
		setVisible( false );
  };


  return (
    <div>
   	<Nav />
      <div className="Banner"/>
      <div className="Card" >

			{articles.map( (article, i ) => {	
				return(

				 <div key={i} style={{display:'flex', justifyContent:'space-around', align:'middle'}}>
					<Card
				      style={{ 
						   width: 300, 
						   margin:'15px', 
						   display:'flex',
						   flexDirection: 'column',
						   justifyContent:'space-between' }}
				      cover={
						   <img
						       alt="example"
						       src={article.urlToImage}
						   />
				      }
				      actions={[
				          <Icon type="read" key="ellipsis2" onClick={showModal} />,
				          <Icon type="like" key="ellipsis" onClick={ () => props.addToWishList( article ) }/>
				      ]}
				  		>addToWishList
						<Meta
						  title={article.title}
						  description={article.description}
						/>

					</Card>

					<Modal
						title={article.source.name}
						visible={visible}
						onOk={handleOk}
						footer={[
							<Button key="okButton" type="primary" onClick={handleOk}>
								Ok
							</Button>,
						]}

					 >
						<Meta 
						  title={article.title}
						  description={article.content}
						/>

					</Modal>

 				 </div>
				);				
			} ) }

		</div>
    </div> 

  );
}


function mapDispatchToProps(dispatch) {
  return {
    addToWishList: function(articleObj) { 
       dispatch( { type: 'addArticle', article: articleObj } ) 
    }
  }
}

export default connect(
    null, 
    mapDispatchToProps
)( ScreenArticlesBySource );
