import React, { useState } from 'react';
import './App.css';
import { Card, Icon, Modal, Button } from 'antd';
import {connect} from 'react-redux';

const { Meta } = Card;


function ScreenMyArticles( props ) {
	const [ visible, setVisible ] = useState(false);

	const showModal = () => {
		setVisible(true);	
	}

	const handleOk = e => {
		//console.log(e);
		setVisible( false );
  };


  return (
	 <div>

		<div className="Banner"/>

		<div className="Card">

			{ props.myArticles.map( (article, i ) => {	
			 return(

				<div  style={{display:'flex',justifyContent:'center'}}>
					<Card
						style={{  
						  width: 300, 
						  margin:'15px', 
						  display:'flex',
						  flexDirection: 'column',
						  justifyContent:'space-between' }}
						cover={
							<img
								 alt="example"
								 src={article.urlToImage}
							/>
						}
						actions={[
							<Icon type="read" key="ellipsis2" onClick={showModal} />,
							<Icon type="delete" key="ellipsis" onClick={ () => props.deleteFromWishList( article ) }/>

						]}
					>

						<Meta
						  title={article.title}
						  description={article.description}
						/>

					</Card>

					<Modal
						title={article.source.name}
						visible={visible}
						onOk={handleOk}
						footer={[
							<Button key="okButton" type="primary" onClick={handleOk}>
								Ok
							</Button>,
						]}

					 >
						<Meta 
						  title={article.title}
						  description={article.content}
						/>

					</Modal>



				</div>
			 ) }
			)}

		</div>

	</div>
  );
}



function mapStateToProps(state) {
  return {
    myArticles: state.articleWishlist
  }
}

function mapDispatchToProps(dispatch) {
  return {
    deleteFromWishList: function(articleObj) { 
       dispatch( { type: 'deleteArticle', article: articleObj } ) 
    }
  }
}

export default connect(
    mapStateToProps, 
    mapDispatchToProps
)( ScreenMyArticles );




