import React, {useState, useEffect} from 'react';
//import {
//	BrowserRouter as Router, Switch, Route,
//	useParams
//} from 'react-router-dom';
import { Card, Icon } from 'antd';
import './App.css';

const { Meta } = Card;


/* Initialize News API */
//const NewsAPI = require('newsapi');
const APIkey = '920fe1871ec5410289dba3174398da12';


function ScreenArticlesBySource(props) {

  const [ articles, setArticles ] = useState([]);
  var source = props.idSource;

useEffect( (source) => {

		async function fetchNews (srcNews) {
			let urlRequestNews = `https://newsapi.org/v2/everything?sources=${props.idSource}&from=2020-02-09&to=2020-02-10&apiKey=${APIkey}`;
			let articles = await fetch( urlRequestNews );
			let response = await articles.json();

			if(response.status === 'ok'){
				console.log(response)
				setArticles( response.articles )
			}
		}
		fetchNews(source);	
	}, [] );


	var articlesList = articles.map( (article, i ) => {

		return(
		 <div className="Card" key={i}>

        <div  style={{display:'flex',justifyContent:'center'}}>

			<Card
            style={{ 
		         width: 300, 
		         margin:'15px', 
		         display:'flex',
		         flexDirection: 'column',
		         justifyContent:'space-between' }}
            cover={
		         <img
		             alt="example"
		             src={article.urlToImage}
		         />
            }
            actions={[
                <Icon type="read" key="ellipsis2" />,
                <Icon type="like" key="ellipsis"/>
            ]}
            >

            <Meta
              title={article.title}
              description={article.description}
            />

          </Card>	
			</div>
		 </div>
		);

	} );


  return (
    <div>

      <div className="Banner"/>

			 {articlesList}

    </div> 

  );
}



export default ScreenArticlesBySource;
