import React from 'react';
//import {
//	BrowserRouter as Router, Switch, Route,
//	useParams
//} from 'react-router-dom';
import { Card, Icon } from 'antd';

import './App.css';

const { Meta } = Card;


/* Initialize News API */
//const NewsAPI = require('newsapi');
const APIkey = '920fe1871ec5410289dba3174398da12';


function ScreenArticlesBySource(props) {


useEffect( () => {

		async function fetchNews () {
			let urlRequestNews = `https://newsapi.org/v2/everything?sources=${props.idSource}&apiKey=${APIkey}`;
			let articles = await fetch( urlRequestNews );
			let response = await articles.json();
			if(response.status === 'ok'){
				setSourceList( response.sources )
			}
		}
		fetchNews();	
	}, [] );


  return (
    <div>

      <div className="Banner"/>

      <div className="Card">

        <div  style={{display:'flex',justifyContent:'center'}}>

          <Card
            style={{ 
            width: 300, 
            margin:'15px', 
            display:'flex',
            flexDirection: 'column',
            justifyContent:'space-between' }}
            cover={
            <img
                alt="example"
                src='../images/alaska.jpg'
            />
            }
            actions={[
                <Icon type="read" key="ellipsis2" />,
                <Icon type="like" key="ellipsis"/>
            ]}
            >

            <Meta
              title='Article title'
              description='Article Description'
            />

          </Card>

        </div>

      </div> 



    </div>
  );
}



export default ScreenArticlesBySource;
