export default function(articleList = [], action) {

	if(action.type === 'addArticle') {

		let articleObj = {id: (articleList.length), ...action.article };
		console.log(  articleObj )

		return [...articleList, articleObj ] ;
	}

	else if(action.type === 'deleteArticle') {

		let articleObj = [...articleList];
		articleObj.splice(action.articleId,1);

		console.log(  articleObj )
		return articleObj ;
	} 
	else {
		return articleList ;
	} 
}
