import React, { useState, useEffect } from 'react';
import { List, Avatar } from 'antd';
import {
	BrowserRouter as Router, Switch, Route, Link,
	useParams
} from 'react-router-dom';

import './App.css';
import ScreenArticlesBySource from './ScreenArticlesBySource';

/* Initialize News API */
//const NewsAPI = require('newsapi');
const APIkey = '920fe1871ec5410289dba3174398da12';


function ScreenSource() {

  const [ sourceList, setSourceList ] = useState([]);

  useEffect( () => {

		async function fetchSources () {
			let urlRequestNews = `https://newsapi.org/v2/sources?country=fr&language=fr&apiKey=${APIkey}`;
			let sources = await fetch( urlRequestNews );
			let response = await sources.json();
			if(response.status === 'ok'){
				setSourceList( response.sources )
			}
		}
		fetchSources();	
	}, [] );

  return (
   <Router>


		<div className="Banner" />

		<div className="HomeThemes">

			<List
				itemLayout="horizontal"
				dataSource={sourceList}
				renderItem={item => (
				  <List.Item>
					 <List.Item.Meta
						avatar={<Avatar src={`/images/${item.category}.png`}/>}
						title={<Link to={`/sources/${item.id}`}>{item.name}</Link>}
						description={item.description}
					 />
				  </List.Item>
				)}
			 />

		</div>  

		<Switch>
			<Route path="/sources/:id" component={<ScreenArticlesBySource />} />
		</Switch>


    </Router>
  );
}



