import React, {useState, useEffect} from 'react';
import { Card, Icon, Modal } from 'antd';

import './App.css';
import Nav from './Nav';

const { Meta } = Card;


/* Initialize News API */
//const NewsAPI = require('newsapi');
const APIkey = '920fe1871ec5410289dba3174398da12';


function ScreenArticlesBySource( props ) {

  const [ articles, setArticles ] = useState([]);


  useEffect( () => {
		async function fetchNews () {
			let urlRequestNews = `https://newsapi.org/v2/everything?sources=${props.match.params.id}&from=2020-02-09&to=2020-02-10&apiKey=${APIkey}`;
			let articles = await fetch( urlRequestNews );
			let response = await articles.json();

			if(response.status === 'ok'){
				//console.log(response)
				setArticles( response.articles )
			}
		}
		fetchNews();	
	}, [props] );





  return (
    <div>
   	<Nav />
      <div className="Banner"/>
      <div className="Card" >

			{articles.map( (article, i ) => {	
				return(

				 <div key={i} style={{display:'flex', justifyContent:'space-around', align:'middle'}}>
					<Card
				      style={{ 
						   width: 300, 
						   margin:'15px', 
						   display:'flex',
						   flexDirection: 'column',
						   justifyContent:'space-between' }}
				      cover={
						   <img
						       alt="example"
						       src={article.urlToImage}
						   />
				      }
				      actions={[
				          <Icon type="read" key="ellipsis2" />,
				          <Icon type="like" key="ellipsis"/>
				      ]}
				  		>
						<Meta
						  title={article.title}
						  description={article.description}
						/>

					</Card>
				 </div>

				);				
			} ) }

		</div>
    </div> 

  );
}




export default ScreenArticlesBySource;
